import React from 'react'

const InputForm = (props) => {

    return (
        <div className="form">
            <input onChange={() => props.onAddChange()} value={props.onAddValue} type="text" />

            <button onClick={() => props.onAddClick()}>
                <span>Add</span>
            </button>

        </div>
    )
}

export default InputForm
